\n    "pubnews-plus::two_column_section_layout": {\n        "value": "boxed--layout",\n        "type": "theme_mod",\n        "user_id": 1,\n        "date_modified_gmt": "2025-03-04 14:00:04"\n    },\n    "pubnews-plus::lefts_rightc_blocks_width_layout": {\n        "value": "boxed--layout",\n        "type": "theme_mod",\n        "user_id": 1,\n        "date_modified_gmt": "2025-03-04 14:01:04"\n    },\n    "pubnews-plus::bottom_full_width_blocks_width_layout": {\n        "value": "boxed--layout",\n        "type": "theme_mod",\n        "user_id": 1,\n        "date_modified_gmt": "2025-03-04 14:01:04"\n    },\n    "pubnews-plus::archive_page_layout": {\n        "value": "two",\n        "type": "theme_mod",\n        "user_id": 1,\n        "date_modified_gmt": "2025-03-04 14:01:04"\n    },\n    "pubnews-plus::archive_width_layout": {\n        "value": "boxed--layout",\n        "type": "theme_mod",\n        "user_id": 1,\n        "date_modified_gmt": "2025-03-04 14:01:04"\n    },\n    "pubnews-plus::footer_section_width": {\n        "value": "boxed-width",\n        "type": "theme_mod",\n        "user_id": 1,\n        "date_modified_gmt": "2025-03-04 14:02:04"\n    },\n    "pubnews-plus::single_page_width_layout": {\n        "value": "boxed--layout",\n        "type": "theme_mod",\n        "user_id": 1,\n        "date_modified_gmt": "2025-03-04 14:02:04"\n    },\n    "pubnews-plus::search_page_width_layout": {\n        "value": "boxed--layout",\n        "type": "theme_mod",\n        "user_id": 1,\n        "date_modified_gmt": "2025-03-04 14:02:04"\n    }\n}', '', '', 'trash', 'closed', 'closed', '', 'ffbda6b5-1f02-48d3-a8d5-04d3488511cc', '', '', '2025-03-04 14:02:05', '2025-03-04 14:02:05', '', 0, 'http://wp-project1.local/?p=116', 0, 'customize_changeset', '', 0),
