	(157, 1, '2025-03-06 16:11:18', '2025-03-06 16:11:18', '<iframe title="ĐỨC CHA PHÊRÔ LÊ TẤN LỢI - GIÁM MỤC CHÍNH TÒA GIÁO PHẬN CẦN THƠ | Ngày 01.01.2025" width="640" height="360" src="https://www.youtube.com/embed/k0B8d9pf9Js?feature=oembed" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" referrerpolicy="strict-origin-when-cross-origin" allowfullscreen></iframe>', '', '', 'publish', 'closed', 'closed', '', '66c00339c3b9b0639ada770f32f4a641', '', '', '2025-03-06 16:11:18', '2025-03-06 16:11:18', '', 0, 'http://wp-project1.local/2025/03/06/66c00339c3b9b0639ada770f32f4a641/', 0, 'oembed_cache', '', 0),
